mapper.select:
select count(*) from record


    and id = #{id,jdbcType=BIGINT}


    and name = #{name,jdbcType=VARCHAR}


    and task_id = #{taskId,jdbcType=BIGINT}


    and path = #{path,jdbcType=VARCHAR}


    and creat_time = #{creatTime,jdbcType=TIMESTAMP}


    and type = #{type,jdbcType=INTEGER}


    and index = #{index,jdbcType=INTEGER}

select 

from record where 1 = 1 

  and id = #{param1.id,jdbcType=BIGINT}


  and name = #{param1.name,jdbcType=VARCHAR}


  and task_id = #{param1.taskId,jdbcType=BIGINT}


  and path = #{param1.path,jdbcType=VARCHAR}


  and creat_time = #{param1.creatTime,jdbcType=TIMESTAMP}


  and type = #{param1.type,jdbcType=INTEGER}


  and index = #{param1.index,jdbcType=INTEGER}


  order by 

    ${order.property} ${order.direction}



  limit ${param2.offset}, ${param2.pageSize}
